import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      const item = action.payload;
    },
    removeItemTocart() {},
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
